#import scipy.signal as signal
import math


def edit_theta(theta0, ranges):
    r = 0.5
    roslen = 0.35
    theta = round(-theta0 / math.pi * 180)
    # ranges = signal.medfilt(ranges, 5)  # 中值滤波，去除噪声
    ranges = [3.5 if i == 0 else i for i in ranges]
    alpha = int((roslen / r) / math.pi * 180) + 1  # 若要机器人通过，最少需要alpha个连续的角度没有障碍（角度之间间隔1°）
    if alpha // 2 == 0:
        alpha += 1  # 为了使0°无障碍时的计算没有偏差，alpha取奇数

    # 从左往右扫描
    ans1 = -10  # 记录从左往右扫描的结果，-1表示无解
    now_angle = int((180 - theta - alpha // 2 + 360) % 360)  # 初始值为中间，从中间往左
    count = 0  # 记录 已经经过的 连续的 没有障碍的 角度 个数
    for ii in range(180 + alpha // 2):
        if ranges[now_angle] < r:
            count = 0
        else:
            count += 1
        if count >= alpha:
            ans1 = (now_angle - alpha // 2 + 360) % 360
            break
        now_angle = (now_angle + 1) % 360

    # 从右往左扫描
    ans2 = -10  # 记录从右往左扫描的结果，-1表示无解
    now_angle = int((180 - theta + alpha // 2 + 360) % 360)  # 初始值为中间，从中间往左
    count = 0  # 记录 已经经过的 连续的 没有障碍的 角度 个数
    count1 = 0
    for ii in range(180 + alpha // 2):
        # while count < alpha and now_angle >= 90 - alpha // 2:
        if ranges[now_angle] < r:
            count = 0
        else:
            count += 1
        if count >= alpha:
            ans2 = (now_angle + alpha // 2) % 360
            break
        now_angle = (now_angle - 1 + 360) % 360

    # 圆上的点不在车子前进方向构成的矩形
    flag1 = 1
    for numflag in range(0, 360):
        if flag1 == 1:
            # flag1初始化为0
            flag1 = 0
            # 判断0-360度的范围内
            for num in range(0, 360):
                count1 += 1
                num1 = (ans1 - 180 + num) % 360
                if ranges[num1] < r:
                    s1 = num1 / 180.0 * math.pi
                    s2 = (int(ans1 - 180 + 360) % 360 / 180.0) * math.pi
                    # 从目标点做平行于小车边缘线的两条直线，原点到两个直线的距离分别是flag2和flag3
                    flag2 = ranges[num1] * (math.sin(s1) * math.cos(s2) - math.sin(s2) * math.cos(s1))
                    flag3 = ranges[num1] * (math.sin(s1) * math.sin(s2) + math.cos(s2) * math.cos(s1))
                    # print(num)
                    # print(flag2)
                    # print(flag3)
                    # 如果到侧面的距离小于车边长的一半，且不在小车朝向的背方向，则表示不能通过，flag1设为1
                    if abs(flag2) < roslen / 2:
                        if flag3 < 0:
                            flag1 = 1
                            break
                    if ans1 < 180:
                        if ranges[num1] < 1.118 * roslen:
                            if num1 < 207:
                                if num1 > ans1 + 26:
                                    flag1 = 1
                                    break
                    else:
                        if ranges[num1] < 1.118 * roslen:
                            if num1 > 153:
                                if num1 < ans1 - 26:
                                    flag1 = 1
                                    break
            # 如果车子前方有点，则角度向两边旋转
            if flag1 == 1:
                ans1 = (ans1 - 1) % 360
        if flag1 == 1:
            if numflag == 359:
                ans1 = -1000

    flag1 = 1
    for numflag in range(0, 360):
        if flag1 == 1:
            # flag1初始化为0
            flag1 = 0
            # 判断0-360度的范围内
            for num in range(0, 360):
                num1 = (ans2 - 180 + num) % 360
                count1 += 1
                if ranges[num1] < r:
                    s1 = num1 / 180.0 * math.pi
                    s2 = (int(ans2 - 180 + 360) % 360 / 180.0) * math.pi
                    # 从目标点做平行于小车边缘线的两条直线，原点到两个直线的距离分别是flag2和flag3
                    flag2 = ranges[num1] * (math.sin(s1) * math.cos(s2) - math.sin(s2) * math.cos(s1))
                    flag3 = ranges[num1] * (math.sin(s1) * math.sin(s2) + math.cos(s2) * math.cos(s1))
                    # print(num)
                    # print(flag2)
                    # print(flag3)
                    # 如果到侧面的距离小于车边长的一半，且不在小车朝向的背方向，则表示不能通过，flag1设为1
                    if abs(flag2) < roslen / 2:
                        if flag3 < 0:
                            flag1 = 1
                            break
                    if ans2 < 180:
                        if ranges[num1] < 1.118 * roslen:
                            if num1 < 207:
                                if num1 > ans2 + 26:
                                    flag1 = 1
                                    break
                    else:
                        if ranges[num1] < 1.118 * roslen:
                            if num1 > 153:
                                if num1 < ans2 - 26:
                                    flag1 = 1
                                    break
            # 如果车子前方有点，则角度向两边旋转
            if flag1 == 1:
                ans2 = (ans2 + 1) % 360
        if flag1 == 1:
            if numflag == 359:
                ans2 = -1000

    if ans1 == -1000:
        ans1 = int(-10)
    else:
        ans1 = (ans1 - 180.0) / 180.0 * math.pi

    if ans2 == -1000:
        ans2 = int(-10)
    else:
        ans2 = (ans2 - 180.0) / 180.0 * math.pi
    print(count1)
    if abs(ans1 - theta0) < abs(ans2 - theta0):
        return ans1
    else:
        return ans2
    # return [ans1,ans2]


